package tarea_de_clase;

public class declaracionfloatydouble {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double pi=3.1415;
		float pii =3.1415f;// en float siempre se finaliza con f
		
		System.out.println("el valor de pi es " + pi);
		System.out.println("el valor de pi es " + pii);

	}

}
